<?phpclass rest_m extends CI_Model {    var $user = 'user';    public function __construct() {        parent::__construct();        $this->load->database();    }            public function check_user($username,$password){        $sql = "SELECT * FROM user U  WHERE  U.phone_number = '$username' AND U.password = '$password' AND U.status = 1";        $query = $this->db->query($sql);        return $query->row();    }    public function user_updated($device_id,$device_type,$user_id) {        $created_date = date('Y-m-d H:i:s', time());        $data = array('device_id'=>$device_id,'device_type'=>$device_type,'last_login_date' => $created_date);        $this->db->where('user_id', $user_id);        $this->db->update('user', $data);    }    public function add_user($data) {        $this->db->insert('user', $data);        return $this->db->insert_id();    }    public function users_check($phone_number) {        $query = $this->db->get_where('user', array('phone_number' => $phone_number));        return $query->row();    }    public function get_users(){        $this->db->select('U.user_id,U.phone_number,U.first_name,U.last_name,U.profile_image,U.email_id,U.country,U.gender,U.designation,U.address,U.hef_member_status,U.chapter_id,U.website,U.company,U.state,U.category,C.chapter_name');        $this->db->join('chapter C', 'C.chapter_id= U.chapter_id', 'left');          //$this->db->order_by('C.chapter_name asc , U.first_name asc');        //$this->db->order_by('(C.chapter_name * -1)', 'ASC' );       // $this->db->order_by('C.chapter_name IS NULL', 'ASC' );        $this->db->order_by('C.chapter_name','ASC' );        $this->db->order_by('U.first_name','ASC');        $query = $this->db->get_where('user U', array('U.status' => 1));        return $query->result();    }                public function get_advertisement(){                $this->db->select('advertisement_id,advertisement_name,advertisement_url,advertisement_image');        $query = $this->db->get_where('advertisement', array('status' => 1));        return $query->result();    }                  public function edit_user($data,$user_id) {        $this->db->where('user_id', $user_id);        $this->db->update('user', $data);        return $user_id;    }                public function add_posts($data){        $this->db->insert('user_posts', $data);        return $this->db->insert_id();    }            public function get_user_post(){            $sql = "SELECT UP.*,U.first_name,U.last_name,U.phone_number,U.email_id FROM user_posts UP , user U WHERE UP.user_id = U.user_id";        $query = $this->db->query($sql);        return $query->result();    }            public function get_chapter(){                                    $this->db->order_by('chapter_name','ASC' );        $query = $this->db->get_where('chapter',array('status'=>1));        return $query->result();    }        public function get_user_divice_id($user_id){        $this->db->select('device_id,device_type');        $query = $this->db->get_where('user',array('user_id !='=>$user_id));        return $query->result();         }         public function get_articles(){          //$this->db->select('articles_id,articles_title,articles_desc');        $query = $this->db->get_where('articles',array('status'=>1));        return $query->result();       }            public function get_message(){          //$this->db->select('message_id,message_title,message_desc');        $query = $this->db->get_where('message',array('status'=>1));        return $query->result();       }    }?>